{
    reserves {
      id
      lTokenInterestIndex
      lastUpdateTimestamp
      borrowAPY
      depositAPY
      totalBorrow
      totalDeposit
      lTokenUserBalanceCount
      dTokenUserBalanceCount
      deposit {
        id
      }
      incentivePool {
        id
      }
      borrow {
        id
        amount
        timestamp
        tokenId
      }
      repay {
        id
        userDTokenBalance
        feeOnCollateralServiceProvider
        timestamp
        tokenId
      }
      reserveHistory(orderBy: timestamp) {
        id
        timestamp
        borrowAPY
        depositAPY
        totalBorrow
        totalDeposit
      }
      lToken {
        id
      }
    }
  }

