const utils = require('../helper/utils');

async function tvl() {
  const {data: { confirmed }} = await utils.fetchURL('https://api.ergoplatform.');

  return {
    ergo: confirmed.nanoErgs / 1e9
  };
}

module.exports = {
    timetravel: false,
    ergo:{
        tvl
    },
    methodology: `SigmaUSD TVL is calculated by calling the sigmaUSD bank (via the ergo blockchain explorer API), this returns the confirmed total within the contract in nanoERGs`
}